import React, {useState} from 'react';
import './App.css';
import Header from './Header';

//estados são informações armazenadas dos componentes. Ex: usuário preencheu um campo
//de formulário. Para guardar o que ele escreveu, é necessário usar estados

function App() {
  const [counter, setCounter] = useState(0); //[valor do estado, função para atualizar o valor estado]

  function handleButtonClick() {
    setCounter(counter + 2); //atribuindo um novo valor ao counter
  }

  return (
    <div>
      <Header title = "Olá"/>
      <h1>{counter}</h1>
      <button type = "button" onClick = {handleButtonClick}>Aumentar</button>
    </div>
  );
}

export default App;
